/**
 * Hook: Load room images
 * Usage: const { images, loading } = useRoomImages(roomTypeId)
 */
export const useRoomImages = (roomTypeId) => {
  const [images, setImages] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        setLoading(true);
        const data = await getRoomImageByType(roomTypeId, supabase);
        setImages(data);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };

    if (roomTypeId) {
      fetchImages();
    }
  }, [roomTypeId]);

/**
 * Notification/Toast System
 * Centralized notification handling for the application
 */

// Store active notifications
let notifications = [];
let notificationId = 0;
let listeners = [];

/**
 * Subscribe to notification changes
 */
export const subscribeToNotifications = (callback) => {
  listeners.push(callback);
  return () => {
    listeners = listeners.filter(l => l !== callback);
  };
};

/**
 * Notify all listeners
 */
const notifyListeners = () => {
  listeners.forEach(listener => listener([...notifications]));
};

/**
 * Show notification
 */
export const showNotification = (message, type = 'info', duration = 3000) => {
  const id = notificationId++;
  const notification = {
    id,
    message,
    type, // 'success', 'error', 'warning', 'info'
    duration
  };

  notifications.push(notification);

